class D{static toVND(t){return new Intl.NumberFormat("vi-VN",{style:"currency",currency:"VND"}).format(t)}static numberToPronunciation(t){const s=["không","một","hai","ba","bốn","năm","sáu","bảy","tám","chín"],c=["","","hai mươi","ba mươi","bốn mươi","năm mươi","sáu mươi","bảy mươi","tám mươi","chín mươi"],l=["","một trăm","hai trăm","ba trăm","bốn trăm","năm trăm","sáu trăm","bảy trăm","tám trăm","chín trăm"],u=["","nghìn","triệu","tỷ"];let i="",o=0;for(;t>0;){const n=t%1e3;if(t=Math.floor(t/1e3),n>0){const a=Math.floor(n/100),e=Math.floor(n%100/10),m=n%10;let r="";a>0&&(r+=l[a]+" "),e>0&&(r+=(e===1?"mười":c[e])+" "),m>0&&(r+=e===1&&m===5?"lăm":s[m]),i=r+" "+u[o]+" "+i}o++}return i.trim()}static toDate(t){return new Date(t).toLocaleDateString("vi-VN")}static toDateTime(t){return`${new Date(t).toLocaleTimeString("vi-VN")} ${new Date(t).toLocaleDateString("vi-VN")}`}}export{D as F};
